 // idiot.c  白痴inherit __DIR__"generic_condition";#include <ansi.h>#include <condition.h>int update_condition(object me, int duration){  int i;          i = me->query("sen");   if( i > 21) {                   me->receive_damage("sen", 20);    }       if( random(7) < 2 ) {           tell_object(me, YEL"你发现天下万理莫过如此，人人不过都是傻子罢了。\n"NOR);      message("vision", me->name() + "目光呆滞，嘴角挂着长长的哈拉子~~~~~~\n",                       environment(me), me);   }       me->apply_condition("idiot", duration); if( duration < 1 )              return 0;       return 1;}
